#nullable enable
using System;

namespace Devian.Network
{
    /// <summary>
    /// Interface for network runtime that handles inbound message dispatch.
    /// Implementations are typically generated by the protocol codegen.
    /// </summary>
    public interface INetRuntime
    {
        /// <summary>
        /// Dispatch an inbound message by opcode.
        /// </summary>
        /// <param name="sessionId">Session identifier for the connection.</param>
        /// <param name="opcode">Message opcode.</param>
        /// <param name="payload">Message payload (zero-copy span).</param>
        /// <returns>True if the message was handled, false otherwise.</returns>
        bool TryDispatchInbound(int sessionId, int opcode, ReadOnlySpan<byte> payload);
    }
}
