/**
 * Encode an array of objects to NDJSON format
 * @param {Array<object>} objects - Array of JSON objects
 * @returns {string} NDJSON string (one JSON object per line)
 */
export function encodeNdjsonFromObjects(objects) {
    if (!objects || objects.length === 0) {
        return '';
    }
    return objects.map(o => JSON.stringify(o)).join('\n');
}

/**
 * Encode an array of JSON strings to NDJSON format
 * @param {Array<string>} lines - Array of already JSON.stringify'd lines
 * @returns {string} NDJSON string (one line per entry)
 */
export function encodeNdjsonFromLines(lines) {
    if (!lines || lines.length === 0) {
        return '';
    }
    return lines.join('\n');
}
